import React, { useState } from "react";
// import axios from "axios";

const Signup = () => {
    const [name,setName] = useState();
    const [email,setEmail] = useState();
    const [password,setPassword] = useState();
    const [age, setAge] = useState();
    const [gender,setGender] = useState();

    const handleData = async()=>{
      console.log(name,email,password,age,gender)
      // const formdata = new FormData();
      // formdata.append("name",name);
      // formdata.append("email",email);
      // formdata.append("password",password);
      // formdata.append("age",age);
      // formdata.append("gender",gender);

      // let response = await axios.post(`http://localhost:5000/auth/register`,formdata);
      // console.log(response.data);
      // localStorage.setItem("user",JSON.stringify(response.data.user));
      // localStorage.setItem("token",response.data.auth);

      let result = await fetch("http://localhost:5000/auth/register",{
        method:"post",
        body:JSON.stringify({name,email,password,age,gender}),
        headers:{
          "Content-Type":"application/json"
        }
      })
      result = await result.json();
      
      localStorage.setItem("user",JSON.stringify(result.user));
      localStorage.setItem("token",JSON.stringify(result.auth));
    }

  return (
    <div className="register">
      <h1>Signup</h1>
      <input type="text" placeholder="Enter name" className="inputBox" onChange={(e)=>setName(e.target.value)} value={name} />

      <input type="text" placeholder="Enter email" className="inputBox" onChange={(e)=>setEmail(e.target.value)} value={email}/>

      <input type="password" placeholder="Enter password" className="inputBox" onChange={(e)=>setPassword(e.target.value)} value={password}/>

      <input type="number" placeholder="Enter age" className="inputBox" onChange={(e)=>setAge(e.target.value)} value={age}/>

      <input type="text" placeholder="Enter gender" className="inputBox" onChange={(e)=>setGender(e.target.value)} value={gender}/>

      <button onClick={handleData} className="appButton">Signup</button>
    </div>
  );
};

export default Signup;
