// Click event and function in ReactJs
import React from "react";
function App(){
    function myfunc(){
        return <h1>Good night!</h1>
    }
    return(
      <>
        <h1>Hello</h1>
        <button onClick={myfunc}>Click me</button>
      </>
    )
}

export default App;
