    return React.createElement("div",null,React.createElement("h1",null,"Hello this is my first div with out jsx"));
    

}
*/


// Click event and function in ReactJs 
// Problem here is that if i click on button and my data variables changes but it shows first value not updated value why? here we use stats to change the state.
// The reason why the data is not re-rendered onto the screen is because of functional components being stateless components. Stateless components cannot change their state during the runtime of the app, which means the components cannot be redrawn while the app is in action. If we console.log the data just after clicking the button by putting the {console.log(data)}, we will get the updated value of that data but we cannot see the updated value on the screen because the state is internally changed but the component did not render again due to its stateless nature.
/*
import React from "react";
function App(){
let data = "Vanita chawla"
    function myfunc(){
        data = "Vini chawla"
        alert("function clicked!")
    }

    return(
      <>
        <h1>{data}</h1>
        <button onClick={myfunc}>Click me</button>
        <button onClick={()=> alert("hi")}>click me</button>
        <button onClick={()=> myfunc()}>click me</button>
      </>
    )
}
*/

//  State with functional component
// State is object in which we store the data
// As in above example when we use variable then component not update with click.
// We use state to update the component, when we click on btn state changes then component updated because it understand state is changed.
// When we use state then it again re-render the the jsx part/functional component when state changes
// Usestate is hook to update the state.
/*
import React,{useState} from "react";
const App=()=>{
    const [data,setData] = useState("Vanita");
    const [count, setCount] = useState(0)
    function updateData(){
        setData("Chawla");
        setCount(count + 1);
    }

    return(
        <>
            <h1>{data}</h1>
            <p>{count}</p>
            <button onClick={updateData}>Click me</button>
        </>
    )
}
*/

// State with class component
// to use the state in class component i make a app component with class component not functional 
// In class component we use state for this we have to follow these steps
// i) create constructor
// ii) call parent constructor for this we use super();
// iii) create state by using this
// To make the function no need to use function keyword and call this function by using arrow function and this variable (e.g. ()=> this.updateData())
// To update the state we write like this  this.setState({data:"chawla"})
import React, { Component } from "react";
class App extends Component{
    constructor(){
        super();
        this.state ={
            data:"Vanita"
        }
    }

    updateData(){
        this.setState({
            data:"chawla"
        })
    }
    render(){
        return(
            <>
            <h1>hello</h1>
            <p>0</p>
            <button onClick={()=> this.updateData()}>Click me</button>
            </>
        )
    }
}



export default App;
