import React, { useEffect, useState } from 'react';
import { useDispatch } from 'react-redux';
import { fetchItems } from '../store/slices/itemSlice';
import { useSelector } from 'react-redux';

function Products() {
    const dispatch = useDispatch();
    const [items,setItems] = useState();
    const state = useSelector((state)=> state);
    console.log(state);
    console.log(state.items);
    console.log(items)
    console.log("state.items.data", state.items.data)

    useEffect(()=>{
        getItems();
    },[])

    const getItems = async()=>{
        const fetchres = await dispatch(fetchItems());
       if(fetchItems.fulfilled.match(fetchres)){
        setItems((prev)=>{
            return fetchres.payload;
        })
       }
       else if(fetchItems.rejected.match(fetchres)){
        return "Error in fetching data"
       }
    }

    
  return (
    <div className='mt-[8rem] grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4 md:w-[90%] mx-auto'>
      {
        state.items.isLoading ? <h1>Loading....</h1>:
      state.items.data?.map((item)=>{
  return(
    <div className='flex justify-center items-center flex-col gap-[0.6rem]'>
    <img className='w-[200px] h-[250px]' src={item.image}/>
    <h1 className='text-[0.9rem] w-[200px]'>{item.title}</h1>
    </div>
  )
          
        })
      }
    </div>
  )
}

export default Products
