import React, { useState } from "react";
import {useNavigate} from "react-router-dom"

const AddRecipe = ()=>{
    const [title,setTitle] = useState();
    const [category,setCategory] = useState();
    const [ingradients, setInGradient] = useState();
    const [newGradient, setNewGradient] = useState();

    const navigate = useNavigate();

    const handleAddIngredient = ()=>{
      setInGradient([...ingradients,newGradient])
      setNewGradient("")
    }


    return(
        <div className="mt-[8rem] w-[100%] sm:w-[90%] mx-auto">
        <h1>Submit Recipe</h1>
       <div className="flex flex-col gap-[2rem] mt-[2rem]">
       <input className="p-[0.8rem] border-solid border-black border-[1px]" type="text" placeholder="Recipe Title" onChange={(e)=> setTitle(e.target.value)} value={title}/>
        <select className="p-[0.8rem] border-solid border-black border-[1px]" name="category" onChange={(e)=> setCategory(e.target.value)} value={category}>
              <option value="select">Choose Category</option>
              <option value="discovery">Discovery</option>
              <option value="wildlife">Wild life</option>
              <option value="food">Food</option>
              <option value="science">Science & technology</option>
              <option value="photography">Photography</option>
              <option value="travel">Travel</option>
              <option value="music">Music</option>
              <option value="sports">Sports</option>
              <option value="world">World</option>
            </select>
          <textarea className="p-[0.8rem] border-solid border-black border-[1px]" rows={8} cols={3} placeholder="Short Summary"></textarea>
        <label className="font-bold">Upload Recipe photo</label> 
        <input type="file"/>
        <input type="text" placeholder="Add Ingredient" onChange={(e)=> setNewGradient(e.target.value)} value={newGradient}/>
        <button className="rounded bg-[black] text-white p-[0.5rem]"
        onClick={handleAddIngredient}>Add</button>
        <ul>
        {
          ingradients?.map((ingradient,index)=>{
            return(
              <>
                <li key={index}>{ingradient}</li>
              </>
            )
          })
        }
        </ul>
        <button className="rounded bg-[black] text-white py-[0.5rem] w-fit px-[1rem]">Submit Recipe</button>
        

       </div>
        </div>
    )
}

export default AddRecipe;
