import React, { useState } from "react";
import axios from "axios";
import {useNavigate,useParams} from "react-router-dom"

const UpdatePost = ()=>{
    const [title,setTitle] = useState();
    const [category,setCategory] = useState();
    const [profile,setProfile] = useState();
    const [body,setBody] = useState();

    const navigate = useNavigate();
    let params = useParams();

    const updatePosts = async()=>{
        let auth = localStorage.getItem("user");
        let userId = JSON.parse(auth)._id;


        let date = new Date();

        let mydate = `${date.toLocaleString("default", { month: "short" })} ${
          date.getDate() < 10 ? "0" + date.getDate() : date.getDate()
        }, ${date.getFullYear()}`;

        let formdata = new FormData();
        formdata.append("userId",userId);
        formdata.append("title",title);
        formdata.append("category",category)
        formdata.append("profile",profile);
        formdata.append("body",body);
        formdata.append("createdAt",mydate);

        let response = await axios.put(`http://localhost:5000/posts/update/${params.id}`,formdata);

        if(response.data){
            alert("Post Added");
            setTitle(""); setCategory(""); setBody("");
            navigate("/")
        }
        
    }

    return(
        <div className="add-posts">
        <h1>Update Post</h1>
        <input type="text" placeholder="Title" className="inputBox" onChange={(e)=> setTitle(e.target.value)} value={title}/>
        <select name="category" className="inputBox" onChange={(e)=> setCategory(e.target.value)} value={category}>
              <option value="discovery">Discovery</option>
              <option value="wildlife">Wild life</option>
              <option value="food">Food</option>
              <option value="science">Science & technology</option>
              <option value="photography">Photography</option>
              <option value="travel">Travel</option>
              <option value="music">Music</option>
              <option value="sports">Sports</option>
              <option value="world">World</option>
            </select>
        <input type="file" name="profile" className="inputBox" onChange={(e)=> setProfile(e.target.files[0])}/>
        <textarea rows={8} cols={3} className="inputBox" placeholder="Body" onChange={(e)=> setBody(e.target.value)} value={body}></textarea>
        <button className="appButton" onClick={updatePosts}>Update Post</button>
        </div>
    )
}

export default UpdatePost;
