import React, { useState } from "react";
import { Link } from "react-router-dom";

const Navbar = () => {
  const [menuBtn, setMenuBtn] = useState(false);

  return (
    <>
      <header className="fixed top-0 left-0 w-full bg-[#FFF] shadow-md z-50">
        <div className="flex items-center justify-between w-full gap-4 py-2 px-8">
          <div>
            <img src="./images/logo.png" alt="" className="w-[90%]" />
          </div>

          <ul
            className={`${menuBtn ? "flex" : "hidden"} md:flex flex-col md:flex-row items-center gap-4 p-1 m-0 absolute md:relative top-full md:top-0 right-0 md:right-0 bg-[#89C6E1] md:bg-transparent w-full md:w-auto transition-all duration-500 ease-in-out`}
          >
            <li className="mx-3 my-2 md:my-0">
              <Link className="text-black no-underline font-normal" to="/">Home</Link>
            </li>
            <li className="mx-3 my-2 md:my-0">
              <Link className="text-black no-underline font-normal" to="/shop">Shop</Link>
            </li>
            <li className="mx-3 my-2 md:my-0">
              <Link className="text-black no-underline font-normal" to="/about">About</Link>
            </li>
            <li className="mx-3 my-2 md:my-0">
              <Link className="text-black no-underline font-normal" to="/contact">Contact</Link>
            </li>
            <li className="mx-3 my-2 md:my-0">
              <Link className="text-black no-underline font-normal" to="/login">Login</Link>
            </li>
            <li className="mx-3 my-2 md:my-0">
              <Link className="text-black no-underline font-normal" to="/signup">Signup</Link>
            </li>
            <li className="mx-3 my-2 md:my-0">
              <Link to="/logout">Logout</Link>
            </li>
          </ul>

          <div
            id="menu-btn"
            onClick={() => setMenuBtn(!menuBtn)}
            className={`${
              menuBtn ? "fas fa-times" : "fas fa-bars"
            } text-white text-xl md:hidden cursor-pointer`}
          ></div>
        </div>
      </header>
    </>
  );
};

export default Navbar;
